sys_template.template:
x_376096_pxp_client_script=/* eslint no-undef: "error" */
function onClick(g_form, GlideAjax, $q) {
    var deferred = $q.defer();
    var ga = new GlideAjax('MYSCRIPTINCLUDE');
    ga.addParam('sysparm_name', 'MYFUNCTION');
    ga.addParam('MYPARAM', g_form.getValue('MYFIELD'));
    ga.getXMLAnswer(function (answer) {
        answer = JSON.parse(answer);
        if (answer && answer.status == 'success') {
            // success path
            deferred.resolve();
        } else {
            g_form.addErrorMessage(answer ? answer.message : 'Invalid JSON received');
            deferred.reject();
        }
    });

    return deferred.promise;
}^EQ
